mapper.select:
select * 
from(
	select t.*, rownum rn
	from(
		select rno, bno, reply, replyer, to_char(replyDate,'yyyy-MM-dd') replyDate, updateDate 
		from tbl_reply
		where bno = #{bno}
		order by rno desc
	)t
)
where rn between #{cri.startNo} and #{cri.endNo}

select count (*) 
from tbl_reply 
where bno=#{bno}
